package com.Arrays;

import java.util.Iterator;

public class SingleDimensionalArraysExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
           Single Dimensional Array
            Syntax:-
                dataType arrayName[] = new dataType[size];
                
                
                
// Array should be declared with a dataType
// Array mandatory should be declared with a Size
// Array index starts with 0
// Array index at a time can be assigned with a single value
// Array is recognized with []
// Array is capable of storing with similar dataType values - it cannot be assigned with differnt dataType values
// Array once declared with a particular size - will not allow to store even a single extra value into it
// Array once created and if not assigned with any value - it give a default value of its dataType
// Array index if not assigned with any value - then wastage of Memory location


        */
		
		short Nikky[]=new short[6];
		
		
		        Nikky[4]=9;
		        Nikky[3]=8;
				Nikky[2]=9;
				Nikky[1]=0;
				Nikky[0]=6;
		
		for(short index=0;index<=5;index++) 
		{
			System.out.println(Nikky[index]);
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
