package com.Lopps;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int a=1; a<=2; a++) { 
			
			//True Block - if the condition is True then the Statements of the block will be executed
			
			System.out.println(a+"="+"Madhu");
		}
		System.out.println("*********************************");
		
		for(int a=1; a>=-5; a=a-1) {
			System.out.println(a+"-"+"sai");
			}
		System.out.println("*********************************");
		
		for(int chinttu=3; chinttu>=-15; chinttu=chinttu-3) {
			
			System.out.println(chinttu+"-"+"Nikky");
		}
		
		System.out.println("*********************************");
		
		for(int j=-4; j>=-10; j=j-2) {
			System.out.println(j+"-"+"saaho");
		}
		System.out.println("*********************************");
		
		for(int s=-2; s<=15; s=s+2) {
			System.out.println(s+"-"+"Darling");
		}
		System.out.println("*********************************");
		
		for(int v=-15; v<=-1; v=v+3) {
			System.out.println(v+"-"+"Loops");
		}
		System.out.println("*********************************");
		
		for(int a=-25; a<=-5; a=a+5) {
			System.out.println(a+"-"+"sai");
		}
		
		
	}

}
