package com.Lopps;

public class WhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var1=3;
		while(var1>=1)
		{ 
			System.out.println(var1+"  "+" madhu");
			var1=var1-1;
		
			}
		System.out.println(" The current value of the variable var1 is ="+var1);
		
		System.out.println("******************************");
		
		while(var1>=-10) {
			
			System.out.println(var1+"  "+" abhi ghadu 10th pass ithada ");
			var1=var1-2;
			}
		System.out.println(" The current value of the variable var1 is ="+var1);
		
		System.out.println("******************************");
		
		int sai=7;
		while(sai<=70) 
		{
			System.out.println(sai+"  "+" i love my Mom & Dad");
			sai=sai+6;
		}
		System.out.println("The current value of the variable sai is ="+sai);
		
		System.out.println("******************************");
		
		while(sai<=121) {
			System.out.println(sai+"  "+" madhu");
			sai=sai+10;
		}
		System.out.println("The current value of the variable sai is ="+sai);
		

		
		
		
		
		
		
		
	}

}
